public class JavaDataTypeProgramPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//byte data type
		byte month = 7;
		System.out.println("Byte Data Type Value is " + month);
		
		//short data quantity
		short quantity = 1000;
		System.out.println("Short Data Type value is " + quantity);

		//integer data type
		int empNum = 12345;
		System.out.println("Integer Data Type value is " + empNum);
		
		// long data type - 'l' needs to be added to the number
		// long data type
		long profit = 3634748446l;
		System.out.println("Long Data Type value is " + profit);
		
		//float data type 
		float pieValue = 3.142548946266548666f;
		System.out.println("Float Data Type Value is " + pieValue);
		
		//Adding cast to the float = after writing the value, it asks to pick float after entering the value
		float pieValue2 = (float) 3.142548946266548666;
		System.out.println("Float Data Type is " + pieValue2);
		
		//double data type
		double pieValue3 = 3.142548946266548666;
		System.out.println("Double Data Type Value is " + pieValue3);
		
		//character data type
		char initial = 'S';
		System.out.println("Character Data Type Value is " + initial);
		
		//boolean data type
		boolean status = true;
		System.out.println("Boolean Data Type Value is " + status);
		
		//string data type - Its a non-primitive data type and string always starts with Capital "S"
		String firstName = "Sushma";
		System.out.println("String Data Type Value is " + firstName);
		
		
	}

}
