package MyPracticeProject;

public class JavaDoWhilePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Do While is used to run a specific code until the certain condition is met
		//number of iteration is not fixed
		//Do While will execute atleast once even if the condition is FALSE
		
		int num=1; //1. initialization is the first step
		do 
		{
			System.out.println(num);
			num++;	//2. increment is the second step	
		}while(num<=10); //3. condition is the third step
		

	}

}
