package MyPracticeProject;

public class JavaForLoopPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//For loop is used to execute a set of instructions repeatedly when some conditions become true
		//Iteration means loop is executing
		
		//program to print from 1 to 10
		//syntax: for(initialization;conditon;increment/decrement)
		
		for(int num=1;num<=10; num++) {
			System.out.println(num);
		}
        //print the number from 10 to 1
		
		for(int num1=10; num1>=1; num1--) {
			System.out.println(num1);
		}
		
		
	}

}
