package MyPracticeProject;

public class JavaVarAndDataTypePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//String Data type
		String strName;
		strName = "Sushma";
		System.out.println("The first name is " + strName );
		
		//Byte Data Type
		Byte bytMonth;
		bytMonth = 12;
		System.out.println("The month number is " + bytMonth );
		
		//Short Data Type
		short shtQuantity;
		shtQuantity = 1002;
		System.out.println("The quantity ordered is " + shtQuantity);
		
		//Long Data Type
		long lngProfit;
		lngProfit = 1234534455L;
		System.out.println("The profit is " + lngProfit);
		
		//Integer Data Type
		int intempNum;
		intempNum = 12345;
		System.out.println("The employee ID is " + intempNum);
		
		//Float Data Type
		float fltpieValue;
		fltpieValue = 3.1445456456454575474f;
		System.out.println("The pie value is " + fltpieValue);
		
		//Double Data Type
		double dblpieValue3;
		dblpieValue3 = 3.1445456456454575474;
		System.out.println("The value of pie " + dblpieValue3);
		
		//Character Data Type
		  char chrInitial;
		  chrInitial = 'S';
		  System.out.println("The initial is " + chrInitial);
		  
		//Boolean Data Type
		  boolean blnStatus;
		  blnStatus = true;
		  System.out.println("The status is " + blnStatus);
		  
		 
		
		
		
	
		
		
		
	}

}
