package MyPracticeProject;

public class JavaWhileLoopPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//While Loop = It is used to run a specific code until a certain condition is met
		//Here number of iteration is not fixed
		//While will execute only if the condition is TRUE
		//syntax: for(initialization;conditon;increment/decrement)
		
		//print number from 1 to 10
		int num = 1;    //1. Step 1 = initialization
		while(num<=10) { //2. Step 2 = condition
			System.out.println(num);
			num++; //3. Step 3 = increment if is is ++, decrement if it is --
		}
		

	}

}
